import React, { useState, useEffect } from 'react';

// Skip to Content Link
export const SkipToContent = () => (
  <a
    href="#main-content"
    className="skip-link sr-only focus:not-sr-only"
    aria-label="Skip to main content"
  >
    Skip to main content
  </a>
);

// High Contrast Toggle
export const HighContrastToggle = () => {
  const [isHighContrast, setIsHighContrast] = useState(false);

  useEffect(() => {
    const saved = localStorage.getItem('highContrast');
    if (saved === 'true') {
      setIsHighContrast(true);
      document.documentElement.classList.add('high-contrast');
    }
  }, []);

  const toggleHighContrast = () => {
    setIsHighContrast(!isHighContrast);
    if (!isHighContrast) {
      document.documentElement.classList.add('high-contrast');
      localStorage.setItem('highContrast', 'true');
    } else {
      document.documentElement.classList.remove('high-contrast');
      localStorage.setItem('highContrast', 'false');
    }
  };

  return (
    <button
      onClick={toggleHighContrast}
      className="flex items-center space-x-2 px-3 py-2 rounded-lg bg-gray-100 hover:bg-gray-200 transition-colors"
      aria-label={`${isHighContrast ? 'Disable' : 'Enable'} high contrast mode`}
    >
      <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 21a4 4 0 01-4-4V5a2 2 0 012-2h4a2 2 0 012 2v12a4 4 0 01-4 4zM21 5a2 2 0 00-2-2h-4a2 2 0 00-2 2v12a4 4 0 004 4h4a2 2 0 002-2V5z" />
      </svg>
      <span className="text-sm font-medium">
        {isHighContrast ? 'High Contrast' : 'Normal Contrast'}
      </span>
    </button>
  );
};

// Font Size Controls
export const FontSizeControls = () => {
  const [fontSize, setFontSize] = useState(100);

  useEffect(() => {
    const saved = localStorage.getItem('fontSize');
    if (saved) {
      setFontSize(parseInt(saved));
      document.documentElement.style.fontSize = `${saved}%`;
    }
  }, []);

  const changeFontSize = (newSize) => {
    setFontSize(newSize);
    document.documentElement.style.fontSize = `${newSize}%`;
    localStorage.setItem('fontSize', newSize.toString());
  };

  const resetFontSize = () => {
    changeFontSize(100);
  };

  return (
    <div className="flex items-center space-x-2">
      <button
        onClick={() => changeFontSize(Math.max(80, fontSize - 10))}
        className="w-8 h-8 rounded-lg bg-gray-100 hover:bg-gray-200 transition-colors flex items-center justify-center"
        aria-label="Decrease font size"
      >
        <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20 12H4" />
        </svg>
      </button>
      
      <span className="text-sm font-medium min-w-[3rem] text-center">{fontSize}%</span>
      
      <button
        onClick={() => changeFontSize(Math.min(150, fontSize + 10))}
        className="w-8 h-8 rounded-lg bg-gray-100 hover:bg-gray-200 transition-colors flex items-center justify-center"
        aria-label="Increase font size"
      >
        <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
        </svg>
      </button>
      
      <button
        onClick={resetFontSize}
        className="px-2 py-1 text-xs bg-gray-100 hover:bg-gray-200 rounded transition-colors"
        aria-label="Reset font size"
      >
        Reset
      </button>
    </div>
  );
};

// Reduced Motion Toggle
export const ReducedMotionToggle = () => {
  const [isReducedMotion, setIsReducedMotion] = useState(false);

  useEffect(() => {
    const saved = localStorage.getItem('reducedMotion');
    if (saved === 'true') {
      setIsReducedMotion(true);
      document.documentElement.classList.add('reduced-motion');
    }
  }, []);

  const toggleReducedMotion = () => {
    setIsReducedMotion(!isReducedMotion);
    if (!isReducedMotion) {
      document.documentElement.classList.add('reduced-motion');
      localStorage.setItem('reducedMotion', 'true');
    } else {
      document.documentElement.classList.remove('reduced-motion');
      localStorage.setItem('reducedMotion', 'false');
    }
  };

  return (
    <button
      onClick={toggleReducedMotion}
      className="flex items-center space-x-2 px-3 py-2 rounded-lg bg-gray-100 hover:bg-gray-200 transition-colors"
      aria-label={`${isReducedMotion ? 'Disable' : 'Enable'} reduced motion`}
    >
      <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
      </svg>
      <span className="text-sm font-medium">
        {isReducedMotion ? 'Reduced Motion' : 'Full Motion'}
      </span>
    </button>
  );
};

// Focus Indicator
export const FocusIndicator = () => {
  useEffect(() => {
    const style = document.createElement('style');
    style.textContent = `
      .focus-visible {
        outline: 2px solid #8b5cf6 !important;
        outline-offset: 2px !important;
      }
      
      .high-contrast .focus-visible {
        outline: 3px solid #000000 !important;
        outline-offset: 3px !important;
      }
    `;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);

  return null;
};

// Accessibility Menu
export const AccessibilityMenu = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="relative">
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="flex items-center space-x-2 px-4 py-2 bg-violet-600 text-white rounded-lg hover:bg-violet-700 transition-colors"
        aria-label="Open accessibility menu"
        aria-expanded={isOpen}
        aria-haspopup="true"
      >
        <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6V4m0 2a2 2 0 100 4m0-4a2 2 0 110 4m-6 8a2 2 0 100-4m0 4a2 2 0 100 4m0-4v2m0-6V4m6 6v10m6-2a2 2 0 100-4m0 4a2 2 0 100 4m0-4v2m0-6V4" />
        </svg>
        <span>Accessibility</span>
        <svg className={`w-4 h-4 transition-transform ${isOpen ? 'rotate-180' : ''}`} fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
        </svg>
      </button>

      {isOpen && (
        <div className="absolute right-0 mt-2 w-80 bg-white rounded-2xl shadow-2xl border border-gray-200 p-6 z-50">
          <div className="space-y-4">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Accessibility Options</h3>
            
            <div className="space-y-3">
              <div>
                <label className="text-sm font-medium text-gray-700 mb-2 block">Font Size</label>
                <FontSizeControls />
              </div>
              
              <div>
                <label className="text-sm font-medium text-gray-700 mb-2 block">Display Options</label>
                <div className="space-y-2">
                  <HighContrastToggle />
                  <ReducedMotionToggle />
                </div>
              </div>
            </div>

            <div className="pt-4 border-t border-gray-200">
              <h4 className="text-sm font-medium text-gray-700 mb-2">Keyboard Navigation</h4>
              <ul className="text-xs text-gray-600 space-y-1">
                <li>• Use Tab to navigate between elements</li>
                <li>• Use Enter or Space to activate buttons</li>
                <li>• Use Escape to close modals</li>
                <li>• Use arrow keys for dropdowns</li>
              </ul>
            </div>

            <div className="pt-4 border-t border-gray-200">
              <h4 className="text-sm font-medium text-gray-700 mb-2">Screen Reader Support</h4>
              <p className="text-xs text-gray-600">
                This website is designed to work with screen readers. 
                Use your screen reader's navigation commands to explore the content.
              </p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// Main Accessibility Features Component
const AccessibilityFeatures = ({ showMenu = true, className = '' }) => {
  return (
    <>
      <FocusIndicator />
      <SkipToContent />
      
      {showMenu && (
        <div className={`fixed top-4 left-4 z-40 ${className}`}>
          <AccessibilityMenu />
        </div>
      )}
    </>
  );
};

export default AccessibilityFeatures;
