import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useToast } from '../contexts/ToastContext';

const UserDashboard = () => {
  const { showSuccess, showError } = useToast();
  const [complaints, setComplaints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all');

  // Mock data for demonstration
  const mockComplaints = [
    {
      id: 1,
      title: 'Broken Street Light on Main Road',
      category: 'streetlight',
      status: 'in-progress',
      priority: 'high',
      location: 'Main Road, Dhaka',
      submittedAt: '2024-01-15T10:30:00Z',
      description: 'Street light has been broken for 3 days, making the area unsafe at night.',
      updates: [
        {
          date: '2024-01-16T14:20:00Z',
          message: 'Complaint assigned to electrical department',
          type: 'status'
        },
        {
          date: '2024-01-17T09:15:00Z',
          message: 'Technician scheduled for repair on January 18th',
          type: 'update'
        }
      ]
    },
    {
      id: 2,
      title: 'Garbage Pile on Sidewalk',
      category: 'garbage',
      status: 'resolved',
      priority: 'medium',
      location: 'Park Street, Dhaka',
      submittedAt: '2024-01-10T08:45:00Z',
      description: 'Large pile of garbage has been accumulating for a week.',
      updates: [
        {
          date: '2024-01-11T11:30:00Z',
          message: 'Complaint assigned to sanitation department',
          type: 'status'
        },
        {
          date: '2024-01-12T16:45:00Z',
          message: 'Garbage cleared successfully',
          type: 'resolution'
        }
      ]
    },
    {
      id: 3,
      title: 'Pothole on Bridge Road',
      category: 'road',
      status: 'pending',
      priority: 'urgent',
      location: 'Bridge Road, Dhaka',
      submittedAt: '2024-01-18T12:20:00Z',
      description: 'Large pothole causing traffic and safety issues.',
      updates: [
        {
          date: '2024-01-18T15:10:00Z',
          message: 'Complaint received and under review',
          type: 'status'
        }
      ]
    }
  ];

  useEffect(() => {
    // Simulate API call
    setTimeout(() => {
      setComplaints(mockComplaints);
      setLoading(false);
    }, 1000);
  }, []);

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending': return 'text-yellow-600 bg-yellow-100';
      case 'in-progress': return 'text-blue-600 bg-blue-100';
      case 'resolved': return 'text-green-600 bg-green-100';
      case 'rejected': return 'text-red-600 bg-red-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'urgent': return 'text-red-800 bg-red-100';
      case 'high': return 'text-red-600 bg-red-50';
      case 'medium': return 'text-yellow-600 bg-yellow-50';
      case 'low': return 'text-green-600 bg-green-50';
      default: return 'text-gray-600 bg-gray-50';
    }
  };

  const getCategoryIcon = (category) => {
    const icons = {
      road: 'ðŸ›£ï¸',
      power: 'âš¡',
      flooding: 'ðŸŒŠ',
      garbage: 'ðŸ—‘ï¸',
      water: 'ðŸ’§',
      streetlight: 'ðŸ’¡',
      safety: 'ðŸš¨',
      other: 'ðŸ“‹'
    };
    return icons[category] || 'ðŸ“‹';
  };

  const filteredComplaints = complaints.filter(complaint => {
    if (filter === 'all') return true;
    return complaint.status === filter;
  });

  const stats = {
    total: complaints.length,
    pending: complaints.filter(c => c.status === 'pending').length,
    inProgress: complaints.filter(c => c.status === 'in-progress').length,
    resolved: complaints.filter(c => c.status === 'resolved').length
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100 flex items-center justify-center">
        <div className="text-center">
          <div className="loading-spinner w-12 h-12 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading your dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold gradient-text mb-2">My Dashboard</h1>
          <p className="text-gray-600">Track your community issues and stay updated on progress</p>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-6 mb-8">
          <div className="card p-6 text-center">
            <div className="text-3xl font-bold text-violet-600 mb-2">{stats.total}</div>
            <div className="text-sm text-gray-600">Total Complaints</div>
          </div>
          <div className="card p-6 text-center">
            <div className="text-3xl font-bold text-yellow-600 mb-2">{stats.pending}</div>
            <div className="text-sm text-gray-600">Pending</div>
          </div>
          <div className="card p-6 text-center">
            <div className="text-3xl font-bold text-blue-600 mb-2">{stats.inProgress}</div>
            <div className="text-sm text-gray-600">In Progress</div>
          </div>
          <div className="card p-6 text-center">
            <div className="text-3xl font-bold text-green-600 mb-2">{stats.resolved}</div>
            <div className="text-sm text-gray-600">Resolved</div>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="flex flex-col sm:flex-row gap-4 mb-8">
          <Link
            to="/complaint/new"
            className="btn-primary inline-flex items-center justify-center"
          >
            <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
            </svg>
            Report New Issue
          </Link>
          
          <button className="btn-secondary">
            <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-4l-4 4m0 0l-4-4m4 4V4" />
            </svg>
            Export History
          </button>
        </div>

        {/* Filter Tabs */}
        <div className="flex flex-wrap gap-2 mb-6">
          {[
            { value: 'all', label: 'All Complaints' },
            { value: 'pending', label: 'Pending' },
            { value: 'in-progress', label: 'In Progress' },
            { value: 'resolved', label: 'Resolved' }
          ].map((tab) => (
            <button
              key={tab.value}
              onClick={() => setFilter(tab.value)}
              className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                filter === tab.value
                  ? 'bg-violet-600 text-white'
                  : 'bg-white text-gray-600 hover:bg-gray-50'
              }`}
            >
              {tab.label}
            </button>
          ))}
        </div>

        {/* Complaints List */}
        <div className="space-y-6">
          {filteredComplaints.length === 0 ? (
            <div className="card p-12 text-center">
              <div className="text-6xl mb-4">ðŸ“‹</div>
              <h3 className="text-xl font-semibold text-gray-900 mb-2">No complaints found</h3>
              <p className="text-gray-600 mb-6">
                {filter === 'all' 
                  ? "You haven't submitted any complaints yet."
                  : `No complaints with status "${filter}" found.`
                }
              </p>
              {filter === 'all' && (
                <Link to="/complaint/new" className="btn-primary">
                  Report Your First Issue
                </Link>
              )}
            </div>
          ) : (
            filteredComplaints.map((complaint) => (
              <div key={complaint.id} className="card p-6">
                <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between mb-4">
                  <div className="flex items-start space-x-4">
                    <div className="text-3xl">{getCategoryIcon(complaint.category)}</div>
                    <div className="flex-1">
                      <h3 className="text-lg font-semibold text-gray-900 mb-1">
                        {complaint.title}
                      </h3>
                      <p className="text-gray-600 text-sm mb-2">{complaint.location}</p>
                      <p className="text-gray-500 text-sm">
                        Submitted on {new Date(complaint.submittedAt).toLocaleDateString()}
                      </p>
                    </div>
                  </div>
                  
                  <div className="flex flex-wrap gap-2 mt-4 lg:mt-0">
                    <span className={`px-3 py-1 rounded-full text-xs font-medium ${getStatusColor(complaint.status)}`}>
                      {complaint.status.replace('-', ' ').toUpperCase()}
                    </span>
                    <span className={`px-3 py-1 rounded-full text-xs font-medium ${getPriorityColor(complaint.priority)}`}>
                      {complaint.priority.toUpperCase()}
                    </span>
                  </div>
                </div>

                <div className="mb-4">
                  <p className="text-gray-700">{complaint.description}</p>
                </div>

                {/* Updates Timeline */}
                <div className="border-t border-gray-200 pt-4">
                  <h4 className="text-sm font-semibold text-gray-700 mb-3">Updates</h4>
                  <div className="space-y-3">
                    {complaint.updates.map((update, index) => (
                      <div key={index} className="flex items-start space-x-3">
                        <div className="w-2 h-2 bg-violet-500 rounded-full mt-2 flex-shrink-0"></div>
                        <div className="flex-1">
                          <p className="text-sm text-gray-700">{update.message}</p>
                          <p className="text-xs text-gray-500">
                            {new Date(update.date).toLocaleString()}
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Action Buttons */}
                <div className="flex flex-wrap gap-2 mt-4 pt-4 border-t border-gray-200">
                  <button className="text-sm text-violet-600 hover:text-violet-700 font-medium">
                    View Details
                  </button>
                  <button className="text-sm text-violet-600 hover:text-violet-700 font-medium">
                    Add Comment
                  </button>
                  {complaint.status === 'resolved' && (
                    <button className="text-sm text-violet-600 hover:text-violet-700 font-medium">
                      Rate Resolution
                    </button>
                  )}
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default UserDashboard;
