// frontend/src/components/Navbar.jsx

import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import SearchBar from './SearchBar';
import NotificationCenter from './NotificationCenter';

const Navbar = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const isLoggedIn = !!localStorage.getItem('token');

  // Mock notifications data
  const notifications = [
    {
      id: 1,
      title: 'Complaint Resolved',
      message: 'Your road repair complaint has been resolved successfully.',
      type: 'success',
      read: false,
      important: true,
      timestamp: new Date(Date.now() - 1000 * 60 * 30) // 30 minutes ago
    },
    {
      id: 2,
      title: 'New Task Assigned',
      message: 'You have been assigned a new volunteer task in your area.',
      type: 'info',
      read: false,
      important: false,
      timestamp: new Date(Date.now() - 1000 * 60 * 60 * 2) // 2 hours ago
    },
    {
      id: 3,
      title: 'System Update',
      message: 'New features have been added to improve your experience.',
      type: 'info',
      read: true,
      important: false,
      timestamp: new Date(Date.now() - 1000 * 60 * 60 * 24) // 1 day ago
    }
  ];

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/');
  };

  const handleSearch = (query) => {
    console.log('Searching for:', query);
    // Implement search functionality
  };

  const handleNotificationClick = (notification) => {
    console.log('Notification clicked:', notification);
    // Handle notification click
  };

  const handleMarkAsRead = (notificationId) => {
    console.log('Mark as read:', notificationId);
    // Implement mark as read functionality
  };

  const handleMarkAllAsRead = () => {
    console.log('Mark all as read');
    // Implement mark all as read functionality
  };

  const getRoleColor = (role) => {
    switch (role) {
      case 'admin': return 'bg-gradient-to-r from-red-500 to-pink-600';
      case 'user': return 'bg-gradient-to-r from-blue-500 to-indigo-600';
      case 'volunteer': return 'bg-gradient-to-r from-emerald-500 to-teal-600';
      case 'ngo': return 'bg-gradient-to-r from-purple-500 to-violet-600';
      default: return 'bg-gradient-to-r from-gray-500 to-gray-600';
    }
  };

  const getRoleDisplayName = (role) => {
    switch (role) {
      case 'admin': return 'Admin';
      case 'user': return 'Citizen';
      case 'volunteer': return 'Volunteer';
      case 'ngo': return 'NGO';
      default: return 'User';
    }
  };

  return (
    <nav className="bg-white/90 backdrop-blur-xl shadow-xl border-b border-white/20 sticky top-0 z-50 transition-all duration-300">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-20">
          {/* Logo */}
          <div className="flex items-center">
            <Link to="/" className="flex items-center space-x-3 group">
              <div className="h-12 w-12 bg-gradient-to-r from-indigo-600 via-purple-600 to-pink-600 rounded-2xl flex items-center justify-center shadow-lg group-hover:shadow-xl transition-all duration-300 group-hover:scale-110 animate-glow">
                <svg className="h-7 w-7 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
                </svg>
              </div>
              <span className="text-2xl font-bold gradient-text group-hover:scale-105 transition-transform duration-300">
                CivicAid
              </span>
            </Link>
          </div>

          {/* Navigation Links - Show when not logged in */}
          {!isLoggedIn && (
            <div className="hidden md:flex items-center space-x-6">
              <Link to="/about" className="nav-link">About</Link>
              <Link to="/services" className="nav-link">Services</Link>
              <Link to="/contact" className="nav-link">Contact</Link>
            </div>
          )}

          {/* Search Bar - Show when logged in */}
          {isLoggedIn && (
            <div className="flex-1 max-w-md mx-8">
              <SearchBar
                placeholder="Search complaints, tasks..."
                onSearch={handleSearch}
                suggestions={['Road Issues', 'Water Problems', 'Electricity', 'Sanitation', 'Street Lights']}
                size="default"
              />
            </div>
          )}

          {/* Right Side */}
          <div className="flex items-center space-x-4">
            {isLoggedIn ? (
              <>
                {/* User Info */}
                <div className="flex items-center space-x-3">
                  <div className={`inline-flex items-center px-4 py-2 rounded-full text-sm font-semibold text-white shadow-lg ${getRoleColor(user.role)}`}>
                    {getRoleDisplayName(user.role)}
                  </div>
                  <span className="text-base font-semibold text-gray-800">
                    {user.name}
                  </span>
                </div>

                {/* Notifications */}
                <NotificationCenter
                  notifications={notifications}
                  onNotificationClick={handleNotificationClick}
                  onMarkAsRead={handleMarkAsRead}
                  onMarkAllAsRead={handleMarkAllAsRead}
                />

                {/* Navigation Links */}
                <div className="flex items-center space-x-2">
                  {user.isAdmin ? (
                    <>
                      <Link to="/admin" className="btn-primary px-4 py-2 text-sm">
                        <svg className="h-4 w-4 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v4a2 2 0 01-2 2H9a2 2 0 01-2-2z" />
                        </svg>
                        Dashboard
                      </Link>
                      <Link to="/admin/complaints" className="btn-secondary px-4 py-2 text-sm">
                        <svg className="h-4 w-4 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
                        </svg>
                        Complaints
                      </Link>
                    </>
                  ) : (
                    <>
                      <Link to="/complaint/new" className="btn-primary px-4 py-2 text-sm">
                        <svg className="h-4 w-4 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                        </svg>
                        New Complaint
                      </Link>
                      <Link to="/complaints" className="btn-secondary px-4 py-2 text-sm">
                        <svg className="h-4 w-4 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
                        </svg>
                        My Complaints
                      </Link>
                    </>
                  )}

                  {/* Logout Button */}
                  <button
                    onClick={handleLogout}
                    className="btn-danger px-4 py-2 text-sm"
                  >
                    <svg className="h-4 w-4 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
                    </svg>
                    Logout
                  </button>
                </div>
              </>
            ) : (
              <div className="flex items-center space-x-4">
                <Link to="/login" className="nav-link">
                  Sign In
                </Link>
                <Link to="/register" className="btn-primary px-6 py-2 text-sm">
                  Get Started
                </Link>
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
