// frontend/src/components/Navbar.jsx

import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../minimalist-modern.css';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const location = useLocation();

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 10);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const isLoggedIn = localStorage.getItem('userInfo');
  const user = isLoggedIn ? JSON.parse(isLoggedIn) : null;

  const navLinks = [
    { to: '/', label: 'Home', icon: 'HOME' },
    { to: '/services', label: 'Services', icon: 'SERVICES' },
    { to: '/about', label: 'About', icon: 'ABOUT' },
    { to: '/contact', label: 'Contact', icon: 'CONTACT' },
    { to: '/demo', label: 'Demo', icon: 'DEMO' }
  ];

  const isActive = (path) => location.pathname === path;

  return (
    <nav className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${
      isScrolled 
        ? 'minimal-nav shadow-minimal' 
        : 'bg-transparent'
    }`}>
      <div className="container-minimal">
        <div className="flex items-center justify-between h-16">
          {/* Minimalist Logo */}
          <Link to="/" className="flex items-center space-x-3 group animate-fade-in">
            <div className="w-8 h-8 bg-accent-600 rounded flex items-center justify-center shadow-minimal group-hover:shadow-minimal-lg transition-all duration-300">
              <span className="text-white font-bold text-sm font-mono">C</span>
            </div>
            <div className="hidden sm:block">
              <h1 className="heading-minimal text-lg text-primary-900">
                CivicAid
              </h1>
              <p className="caption-minimal text-xs text-muted">CIVIC PLATFORM</p>
            </div>
          </Link>

          {/* Enhanced Desktop Navigation */}
          <div className="hidden lg:flex items-center space-x-2">
            {navLinks.map((link, index) => (
              <Link
                key={link.to}
                to={link.to}
                className={`modern-nav-link ${
                  isActive(link.to) ? 'active' : ''
                } animate-fade-in-up`}
                style={{animationDelay: `${index * 0.1}s`}}
              >
                <span className="mr-2 text-lg">{link.icon}</span>
                {link.label}
              </Link>
            ))}
          </div>

          {/* Enhanced Auth Buttons / User Menu */}
          <div className="hidden lg:flex items-center space-x-4 animate-fade-in-right">
            {isLoggedIn ? (
              <div className="flex items-center space-x-4">
                <Link to="/dashboard" className="modern-btn modern-btn-primary text-sm px-6 py-3">
                  <span className="mr-2">üìä</span>
                  Dashboard
                </Link>
                <div className="relative group">
                  <button className="flex items-center space-x-3 p-3 rounded-2xl hover:bg-gray-100/80 transition-all duration-300 group-hover:scale-105">
                    <div className="w-10 h-10 bg-gradient-to-r from-violet-500 to-purple-600 rounded-full flex items-center justify-center text-white font-bold text-sm shadow-lg group-hover:shadow-xl transition-all duration-300">
                      {user?.name?.charAt(0) || 'U'}
                    </div>
                    <span className="text-gray-700 font-semibold">{user?.name || 'User'}</span>
                    <svg className="w-5 h-5 text-gray-500 group-hover:text-violet-600 transition-colors duration-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                    </svg>
                  </button>

                  {/* Enhanced Dropdown Menu */}
                  <div className="absolute right-0 mt-3 w-56 modern-card opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all duration-500 transform scale-95 group-hover:scale-100">
                    <div className="py-3">
                      <div className="px-4 py-2 border-b border-gray-100">
                        <p className="text-sm font-semibold text-gray-800">{user?.name || 'User'}</p>
                        <p className="text-xs text-gray-500">{user?.email || 'user@example.com'}</p>
                      </div>
                      <Link to="/profile" className="block px-4 py-3 text-gray-700 hover:bg-violet-50 hover:text-violet-600 transition-all duration-300 flex items-center">
                        <span className="mr-3">üë§</span>
                        Profile
                      </Link>
                      <Link to="/settings" className="block px-4 py-3 text-gray-700 hover:bg-violet-50 hover:text-violet-600 transition-all duration-300 flex items-center">
                        <span className="mr-3">‚öôÔ∏è</span>
                        Settings
                      </Link>
                      <hr className="my-2 border-gray-200" />
                      <button
                        onClick={() => {
                          localStorage.removeItem('userInfo');
                          window.location.reload();
                        }}
                        className="block w-full text-left px-4 py-3 text-red-600 hover:bg-red-50 transition-all duration-300 flex items-center"
                      >
                        <span className="mr-3">üö™</span>
                        Logout
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ) : (
              <div className="flex items-center space-x-4">
                <Link to="/login" className="modern-btn modern-btn-secondary text-sm px-6 py-3">
                  <span className="mr-2">üîê</span>
                  Login
                </Link>
                <Link to="/register" className="modern-btn modern-btn-primary text-sm px-6 py-3">
                  <span className="mr-2">‚ú®</span>
                  Sign Up
                </Link>
              </div>
            )}
          </div>

          {/* Mobile Menu Button */}
          <button
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            className="lg:hidden p-2 rounded-xl hover:bg-gray-100 transition-all duration-300"
            aria-label="Toggle menu"
          >
            <div className="w-6 h-6 flex flex-col justify-center items-center">
              <span className={`block w-5 h-0.5 bg-gray-700 transition-all duration-300 ${
                isMenuOpen ? 'rotate-45 translate-y-1' : '-translate-y-1'
              }`}></span>
              <span className={`block w-5 h-0.5 bg-gray-700 transition-all duration-300 ${
                isMenuOpen ? 'opacity-0' : 'opacity-100'
              }`}></span>
              <span className={`block w-5 h-0.5 bg-gray-700 transition-all duration-300 ${
                isMenuOpen ? '-rotate-45 -translate-y-1' : 'translate-y-1'
              }`}></span>
            </div>
          </button>
        </div>

        {/* Mobile Menu */}
        <div className={`lg:hidden transition-all duration-500 overflow-hidden ${
          isMenuOpen ? 'max-h-96 opacity-100' : 'max-h-0 opacity-0'
        }`}>
          <div className="py-4 space-y-2">
            {navLinks.map((link) => (
              <Link
                key={link.to}
                to={link.to}
                onClick={() => setIsMenuOpen(false)}
                className={`block px-4 py-3 rounded-xl text-gray-700 hover:text-violet-600 hover:bg-violet-50 transition-all duration-300 font-medium ${
                  isActive(link.to) ? 'bg-violet-50 text-violet-600' : ''
                }`}
              >
                <span className="mr-3">{link.icon}</span>
                {link.label}
              </Link>
            ))}

            <hr className="border-gray-200 my-2" />

            {isLoggedIn ? (
              <div className="space-y-2">
                <Link
                  to="/dashboard"
                  onClick={() => setIsMenuOpen(false)}
                  className="block px-4 py-3 bg-gradient-to-r from-violet-600 to-purple-700 text-white rounded-xl font-semibold text-center"
                >
                  Dashboard
                </Link>
                <div className="flex items-center px-4 py-3">
                  <div className="w-8 h-8 bg-gradient-to-r from-violet-500 to-purple-600 rounded-full flex items-center justify-center text-white font-semibold text-sm mr-3">
                    {user?.name?.charAt(0) || 'U'}
                  </div>
                  <span className="text-gray-700 font-medium">{user?.name || 'User'}</span>
                </div>
                <button
                  onClick={() => {
                    localStorage.removeItem('userInfo');
                    setIsMenuOpen(false);
                    window.location.reload();
                  }}
                  className="block w-full text-left px-4 py-3 text-red-600 hover:bg-red-50 rounded-xl transition-colors"
                >
                  Logout
                </button>
              </div>
            ) : (
              <div className="space-y-2">
                <Link
                  to="/login"
                  onClick={() => setIsMenuOpen(false)}
                  className="block px-4 py-3 bg-white border-2 border-violet-300 text-violet-700 rounded-xl font-semibold text-center hover:bg-violet-50 transition-colors"
                >
                  Login
                </Link>
                <Link
                  to="/register"
                  onClick={() => setIsMenuOpen(false)}
                  className="block px-4 py-3 bg-gradient-to-r from-violet-600 to-purple-700 text-white rounded-xl font-semibold text-center"
                >
                  Sign Up
                </Link>
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
