import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Navbar from '../components/Navbar';
import ProgressTracker from '../components/ProgressTracker';

const Services = () => {
  const [activeTab, setActiveTab] = useState('citizens');

  const services = {
    citizens: [
      {
        icon: 'üì±',
        title: 'Smart Reporting',
        description: 'Report civic issues with our intelligent form system that guides you through the process.',
        features: ['Photo uploads', 'Location tagging', 'Priority selection', 'Real-time updates']
      },
      {
        icon: 'üìä',
        title: 'Progress Tracking',
        description: 'Monitor the status of your complaints in real-time with detailed progress updates.',
        features: ['Status notifications', 'Timeline view', 'Resolution tracking', 'Feedback system']
      },
      {
        icon: 'ü§ù',
        title: 'Community Connect',
        description: 'Connect with other citizens and volunteers working on similar issues in your area.',
        features: ['Community forums', 'Volunteer matching', 'Issue collaboration', 'Local groups']
      }
    ],
    volunteers: [
      {
        icon: 'üéØ',
        title: 'Task Management',
        description: 'Receive and manage volunteer tasks with our comprehensive task management system.',
        features: ['Task assignments', 'Progress tracking', 'Skill matching', 'Reward system']
      },
      {
        icon: 'üìà',
        title: 'Impact Analytics',
        description: 'Track your volunteer impact with detailed analytics and community contribution metrics.',
        features: ['Hours tracking', 'Impact metrics', 'Achievement badges', 'Community recognition']
      },
      {
        icon: 'üåê',
        title: 'Network Building',
        description: 'Connect with other volunteers and build a strong community network.',
        features: ['Volunteer directory', 'Team formation', 'Mentorship programs', 'Training resources']
      }
    ],
    ngos: [
      {
        icon: 'üè¢',
        title: 'Organization Dashboard',
        description: 'Manage your NGO operations with our comprehensive dashboard and analytics.',
        features: ['Project management', 'Volunteer coordination', 'Resource allocation', 'Impact reporting']
      },
      {
        icon: 'üìã',
        title: 'Grant Management',
        description: 'Streamline grant applications and funding management with our integrated system.',
        features: ['Grant tracking', 'Application forms', 'Document management', 'Reporting tools']
      },
      {
        icon: 'ü§≤',
        title: 'Community Outreach',
        description: 'Expand your reach and connect with communities that need your services.',
        features: ['Community mapping', 'Outreach campaigns', 'Partnership tools', 'Impact measurement']
      }
    ],
    authorities: [
      {
        icon: 'üìä',
        title: 'Issue Management',
        description: 'Efficiently manage and prioritize civic issues with our advanced management system.',
        features: ['Issue prioritization', 'Resource allocation', 'Team assignment', 'Progress monitoring']
      },
      {
        icon: 'üìà',
        title: 'Analytics & Reporting',
        description: 'Get comprehensive insights into community issues and resolution performance.',
        features: ['Real-time analytics', 'Performance metrics', 'Trend analysis', 'Custom reports']
      },
      {
        icon: 'üîó',
        title: 'Stakeholder Communication',
        description: 'Improve communication with citizens, volunteers, and other stakeholders.',
        features: ['Automated updates', 'Multi-channel communication', 'Feedback collection', 'Transparency tools']
      }
    ]
  };

  const pricingPlans = [
    {
      name: 'Basic',
      price: 'Free',
      description: 'Perfect for individual citizens',
      features: [
        'Report up to 10 issues per month',
        'Basic progress tracking',
        'Email notifications',
        'Community access'
      ],
      popular: false
    },
    {
      name: 'Pro',
      price: '$9.99',
      period: '/month',
      description: 'Great for active community members',
      features: [
        'Unlimited issue reporting',
        'Advanced tracking features',
        'Priority support',
        'Detailed analytics',
        'Volunteer coordination'
      ],
      popular: true
    },
    {
      name: 'Enterprise',
      price: 'Custom',
      description: 'For organizations and authorities',
      features: [
        'All Pro features',
        'Custom integrations',
        'Dedicated support',
        'Advanced analytics',
        'API access',
        'White-label options'
      ],
      popular: false
    }
  ];

  const testimonials = [
    {
      name: 'Sarah Johnson',
      role: 'Community Leader',
      image: 'https://images.unsplash.com/photo-1494790108755-2616b612b786?w=150&h=150&fit=crop&crop=face',
      quote: 'CivicAid has transformed how our community addresses local issues. The platform makes it so easy to report problems and track their resolution.',
      rating: 5
    },
    {
      name: 'Michael Chen',
      role: 'Volunteer Coordinator',
      image: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=150&h=150&fit=crop&crop=face',
      quote: 'The volunteer management features are incredible. We can now efficiently coordinate hundreds of volunteers and track their impact.',
      rating: 5
    },
    {
      name: 'Emily Rodriguez',
      role: 'City Official',
      image: 'https://images.unsplash.com/photo-1438761681033-6461ffad8d80?w=150&h=150&fit=crop&crop=face',
      quote: 'CivicAid has improved our response times by 60%. The analytics help us allocate resources more effectively.',
      rating: 5
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100 overflow-hidden">
      <Navbar />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12 relative z-10">
        {/* Hero Section */}
        <div className="text-center mb-16">
          <div className="mb-8">
            <div className="mx-auto h-20 w-20 bg-gradient-to-r from-indigo-600 via-purple-600 to-pink-600 rounded-3xl flex items-center justify-center mb-6 shadow-xl animate-float">
              <span className="text-3xl">‚öôÔ∏è</span>
            </div>
            <h1 className="text-5xl md:text-6xl font-bold text-gray-900 mb-6">
              Our <span className="gradient-text">Services</span>
            </h1>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto leading-relaxed">
              Discover how CivicAid empowers different stakeholders to create better communities. 
              From citizens to authorities, we have solutions for everyone.
            </p>
          </div>
        </div>

        {/* Service Tabs */}
        <div className="mb-16">
          <div className="flex flex-wrap justify-center gap-4 mb-8">
            {Object.keys(services).map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`px-6 py-3 rounded-2xl font-semibold transition-all duration-300 transform hover:scale-105 ${
                  activeTab === tab
                    ? 'bg-gradient-to-r from-indigo-600 to-purple-600 text-white shadow-lg'
                    : 'bg-white/80 text-gray-700 hover:bg-white shadow-md'
                }`}
              >
                {tab.charAt(0).toUpperCase() + tab.slice(1)}
              </button>
            ))}
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {services[activeTab].map((service, index) => (
              <div key={index} className="card p-8 group hover:scale-105 transition-all duration-300">
                <div className="text-4xl mb-6">{service.icon}</div>
                <h3 className="text-xl font-bold text-gray-900 mb-4">{service.title}</h3>
                <p className="text-gray-600 mb-6 leading-relaxed">{service.description}</p>
                <ul className="space-y-2">
                  {service.features.map((feature, featureIndex) => (
                    <li key={featureIndex} className="flex items-center text-sm text-gray-600">
                      <svg className="h-4 w-4 text-emerald-500 mr-2 flex-shrink-0" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                      </svg>
                      {feature}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>

        {/* How It Works */}
        <div className="mb-16">
          <h2 className="text-3xl font-bold text-gray-900 text-center mb-8">How It Works</h2>
          <div className="bg-white/80 backdrop-blur-xl rounded-3xl p-8 shadow-xl border border-white/20">
            <ProgressTracker 
              steps={[
                { title: 'Report', description: 'Submit your issue' },
                { title: 'Review', description: 'We analyze and prioritize' },
                { title: 'Assign', description: 'Task assigned to team' },
                { title: 'Resolve', description: 'Issue gets fixed' }
              ]} 
              currentStep={1} 
              showLabels={true}
            />
          </div>
        </div>

        {/* Pricing Section */}
        <div className="mb-16">
          <h2 className="text-3xl font-bold text-gray-900 text-center mb-12">Choose Your Plan</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {pricingPlans.map((plan, index) => (
              <div key={index} className={`card p-8 relative ${plan.popular ? 'ring-2 ring-indigo-500 scale-105' : ''}`}>
                {plan.popular && (
                  <div className="absolute -top-4 left-1/2 transform -translate-x-1/2">
                    <span className="bg-gradient-to-r from-indigo-600 to-purple-600 text-white px-4 py-2 rounded-full text-sm font-semibold">
                      Most Popular
                    </span>
                  </div>
                )}
                <div className="text-center mb-6">
                  <h3 className="text-2xl font-bold text-gray-900 mb-2">{plan.name}</h3>
                  <div className="mb-4">
                    <span className="text-4xl font-bold text-gray-900">{plan.price}</span>
                    {plan.period && <span className="text-gray-600">{plan.period}</span>}
                  </div>
                  <p className="text-gray-600">{plan.description}</p>
                </div>
                <ul className="space-y-3 mb-8">
                  {plan.features.map((feature, featureIndex) => (
                    <li key={featureIndex} className="flex items-center text-sm">
                      <svg className="h-4 w-4 text-emerald-500 mr-3 flex-shrink-0" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                      </svg>
                      {feature}
                    </li>
                  ))}
                </ul>
                <button className={`w-full py-3 rounded-2xl font-semibold transition-all duration-300 transform hover:scale-105 ${
                  plan.popular 
                    ? 'btn-primary' 
                    : 'bg-white border-2 border-indigo-600 text-indigo-600 hover:bg-indigo-600 hover:text-white'
                }`}>
                  {plan.name === 'Enterprise' ? 'Contact Sales' : 'Get Started'}
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Testimonials */}
        <div className="mb-16">
          <h2 className="text-3xl font-bold text-gray-900 text-center mb-12">What Our Users Say</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {testimonials.map((testimonial, index) => (
              <div key={index} className="card p-6 group hover:scale-105 transition-all duration-300">
                <div className="flex items-center mb-4">
                  <img 
                    src={testimonial.image} 
                    alt={testimonial.name}
                    className="w-12 h-12 rounded-full object-cover mr-4"
                  />
                  <div>
                    <h4 className="font-semibold text-gray-900">{testimonial.name}</h4>
                    <p className="text-sm text-gray-600">{testimonial.role}</p>
                  </div>
                </div>
                <div className="flex mb-4">
                  {[...Array(testimonial.rating)].map((_, i) => (
                    <svg key={i} className="h-5 w-5 text-yellow-400" fill="currentColor" viewBox="0 0 20 20">
                      <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                    </svg>
                  ))}
                </div>
                <p className="text-gray-600 italic">"{testimonial.quote}"</p>
              </div>
            ))}
          </div>
        </div>

        {/* CTA Section */}
        <div className="text-center">
          <div className="bg-gradient-to-r from-indigo-600 via-purple-600 to-pink-600 rounded-3xl p-12 text-white shadow-2xl">
            <h2 className="text-4xl font-bold mb-4">Ready to Transform Your Community?</h2>
            <p className="text-xl mb-8 opacity-90 max-w-2xl mx-auto">
              Join thousands of users who are already making a difference with CivicAid. 
              Start your journey today and help build better neighborhoods.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link
                to="/register"
                className="inline-flex items-center px-10 py-4 bg-white text-indigo-600 font-semibold rounded-2xl hover:bg-gray-100 transition-all duration-300 transform hover:scale-105 shadow-lg"
              >
                Start Free Trial
                <svg className="ml-3 h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7l5 5m0 0l-5 5m5-5H6" />
                </svg>
              </Link>
              <Link
                to="/contact"
                className="inline-flex items-center px-10 py-4 border-2 border-white text-white font-semibold rounded-2xl hover:bg-white hover:text-indigo-600 transition-all duration-300 transform hover:scale-105"
              >
                Schedule Demo
                <svg className="ml-3 h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 10l4.553-2.276A1 1 0 0121 8.618v6.764a1 1 0 01-1.447.894L15 14M5 18h8a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v8a2 2 0 002 2z" />
                </svg>
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Services;
