// src/pages/ViewComplaints.jsx

import React, { useState, useEffect } from 'react';
import { useToast } from '../contexts/ToastContext';

const ViewComplaints = () => {
  const { showSuccess, showError } = useToast();
  const [complaints, setComplaints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    status: 'all',
    category: 'all',
    priority: 'all',
    dateRange: 'all'
  });
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedComplaints, setSelectedComplaints] = useState([]);

  // Mock data for demonstration
  const mockComplaints = [
    {
      id: 1,
      title: 'Broken Street Light on Main Road',
      category: 'streetlight',
      status: 'pending',
      priority: 'high',
      location: 'Main Road, Dhaka',
      submittedAt: '2024-01-18T10:30:00Z',
      submittedBy: 'Ahmed Khan',
      contactInfo: '+880 1712 345 678',
      description: 'Street light has been broken for 3 days, making the area unsafe at night.',
      assignedTo: null,
      updates: [
        {
          date: '2024-01-18T15:10:00Z',
          message: 'Complaint received and under review',
          type: 'status'
        }
      ]
    },
    {
      id: 2,
      title: 'Garbage Pile on Sidewalk',
      category: 'garbage',
      status: 'in-progress',
      priority: 'medium',
      location: 'Park Street, Dhaka',
      submittedAt: '2024-01-17T14:20:00Z',
      submittedBy: 'Fatima Begum',
      contactInfo: '+880 1812 345 678',
      description: 'Large pile of garbage has been accumulating for a week.',
      assignedTo: 'Sanitation Team A',
      updates: [
        {
          date: '2024-01-17T16:30:00Z',
          message: 'Complaint assigned to sanitation department',
          type: 'status'
        },
        {
          date: '2024-01-18T09:15:00Z',
          message: 'Team dispatched to location',
          type: 'update'
        }
      ]
    },
    {
      id: 3,
      title: 'Pothole on Bridge Road',
      category: 'road',
      status: 'pending',
      priority: 'urgent',
      location: 'Bridge Road, Dhaka',
      submittedAt: '2024-01-18T12:20:00Z',
      submittedBy: 'Rahim Ali',
      contactInfo: '+880 1912 345 678',
      description: 'Large pothole causing traffic and safety issues.',
      assignedTo: null,
      updates: [
        {
          date: '2024-01-18T15:10:00Z',
          message: 'Complaint received and under review',
          type: 'status'
        }
      ]
    },
    {
      id: 4,
      title: 'Power Outage in Residential Area',
      category: 'power',
      status: 'resolved',
      priority: 'high',
      location: 'Residential Area, Dhaka',
      submittedAt: '2024-01-16T08:45:00Z',
      submittedBy: 'Sara Ahmed',
      contactInfo: '+880 1612 345 678',
      description: 'Power outage affecting 50+ households for 6 hours.',
      assignedTo: 'Electrical Team B',
      updates: [
        {
          date: '2024-01-16T10:30:00Z',
          message: 'Complaint assigned to electrical department',
          type: 'status'
        },
        {
          date: '2024-01-16T14:45:00Z',
          message: 'Power restored successfully',
          type: 'resolution'
        }
      ]
    },
    {
      id: 5,
      title: 'Flooding in Low-lying Area',
      category: 'flooding',
      status: 'in-progress',
      priority: 'urgent',
      location: 'Low-lying Area, Dhaka',
      submittedAt: '2024-01-18T06:30:00Z',
      submittedBy: 'Mohammad Rahman',
      contactInfo: '+880 1512 345 678',
      description: 'Heavy rainfall causing flooding in residential area.',
      assignedTo: 'Emergency Response Team',
      updates: [
        {
          date: '2024-01-18T07:15:00Z',
          message: 'Emergency response team dispatched',
          type: 'status'
        },
        {
          date: '2024-01-18T08:30:00Z',
          message: 'Water pumps deployed to location',
          type: 'update'
        }
      ]
    }
  ];

  useEffect(() => {
    // Simulate API call
    setTimeout(() => {
      setComplaints(mockComplaints);
      setLoading(false);
    }, 1000);
  }, []);

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending': return 'text-yellow-600 bg-yellow-100';
      case 'in-progress': return 'text-blue-600 bg-blue-100';
      case 'resolved': return 'text-green-600 bg-green-100';
      case 'rejected': return 'text-red-600 bg-red-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'urgent': return 'text-red-800 bg-red-100';
      case 'high': return 'text-red-600 bg-red-50';
      case 'medium': return 'text-yellow-600 bg-yellow-50';
      case 'low': return 'text-green-600 bg-green-50';
      default: return 'text-gray-600 bg-gray-50';
    }
  };

  const getCategoryIcon = (category) => {
    const icons = {
      road: 'ðŸ›£ï¸',
      power: 'âš¡',
      flooding: 'ðŸŒŠ',
      garbage: 'ðŸ—‘ï¸',
      water: 'ðŸ’§',
      streetlight: 'ðŸ’¡',
      safety: 'ðŸš¨',
      other: 'ðŸ“‹'
    };
    return icons[category] || 'ðŸ“‹';
  };

  const filteredComplaints = complaints.filter(complaint => {
    const matchesStatus = filters.status === 'all' || complaint.status === filters.status;
    const matchesCategory = filters.category === 'all' || complaint.category === filters.category;
    const matchesPriority = filters.priority === 'all' || complaint.priority === filters.priority;
    const matchesSearch = complaint.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         complaint.location.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         complaint.submittedBy.toLowerCase().includes(searchTerm.toLowerCase());

    return matchesStatus && matchesCategory && matchesPriority && matchesSearch;
  });

  const handleBulkAction = (action) => {
    if (selectedComplaints.length === 0) {
      showError('Please select complaints first');
      return;
    }

    switch (action) {
      case 'assign':
        showSuccess(`Assigned ${selectedComplaints.length} complaints to team`);
        break;
      case 'update-status':
        showSuccess(`Updated status for ${selectedComplaints.length} complaints`);
        break;
      case 'export':
        showSuccess(`Exported ${selectedComplaints.length} complaints`);
        break;
      default:
        break;
    }
    setSelectedComplaints([]);
  };

  const handleSelectAll = (checked) => {
    if (checked) {
      setSelectedComplaints(filteredComplaints.map(c => c.id));
    } else {
      setSelectedComplaints([]);
    }
  };

  const handleSelectComplaint = (complaintId, checked) => {
    if (checked) {
      setSelectedComplaints(prev => [...prev, complaintId]);
    } else {
      setSelectedComplaints(prev => prev.filter(id => id !== complaintId));
    }
  };

  const handleUpdateStatus = (complaintId, newStatus) => {
    setComplaints(prev => prev.map(c => 
      c.id === complaintId ? { ...c, status: newStatus } : c
    ));
    showSuccess(`Status updated to ${newStatus} for complaint #${complaintId}`);
  };

  const handleAssignTask = (complaintId) => {
    showSuccess(`Task assigned for complaint #${complaintId}`);
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100 flex items-center justify-center">
        <div className="text-center">
          <div className="loading-spinner w-12 h-12 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading complaints...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold gradient-text mb-2">Complaint Management</h1>
          <p className="text-gray-600">Manage and track all community complaints across Bangladesh</p>
        </div>

        {/* Filters and Search */}
        <div className="card p-6 mb-6">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4 mb-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Status</label>
              <select
                value={filters.status}
                onChange={(e) => setFilters(prev => ({ ...prev, status: e.target.value }))}
                className="input-field"
              >
                <option value="all">All Status</option>
                <option value="pending">Pending</option>
                <option value="in-progress">In Progress</option>
                <option value="resolved">Resolved</option>
                <option value="rejected">Rejected</option>
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Category</label>
              <select
                value={filters.category}
                onChange={(e) => setFilters(prev => ({ ...prev, category: e.target.value }))}
                className="input-field"
              >
                <option value="all">All Categories</option>
                <option value="road">Road Issues</option>
                <option value="power">Power Outage</option>
                <option value="flooding">Flooding</option>
                <option value="garbage">Garbage Collection</option>
                <option value="water">Water Supply</option>
                <option value="streetlight">Street Light</option>
                <option value="safety">Public Safety</option>
                <option value="other">Other</option>
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Priority</label>
              <select
                value={filters.priority}
                onChange={(e) => setFilters(prev => ({ ...prev, priority: e.target.value }))}
                className="input-field"
              >
                <option value="all">All Priorities</option>
                <option value="urgent">Urgent</option>
                <option value="high">High</option>
                <option value="medium">Medium</option>
                <option value="low">Low</option>
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Date Range</label>
              <select
                value={filters.dateRange}
                onChange={(e) => setFilters(prev => ({ ...prev, dateRange: e.target.value }))}
                className="input-field"
              >
                <option value="all">All Time</option>
                <option value="today">Today</option>
                <option value="week">This Week</option>
                <option value="month">This Month</option>
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Search</label>
              <input
                type="text"
                placeholder="Search complaints..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="input-field"
              />
            </div>
          </div>

          {/* Bulk Actions */}
          {selectedComplaints.length > 0 && (
            <div className="flex items-center space-x-4 pt-4 border-t border-gray-200">
              <span className="text-sm text-gray-600">
                {selectedComplaints.length} complaint(s) selected
              </span>
              <button
                onClick={() => handleBulkAction('assign')}
                className="text-sm bg-violet-600 text-white px-3 py-1 rounded hover:bg-violet-700 transition-colors"
              >
                Assign to Team
              </button>
              <button
                onClick={() => handleBulkAction('update-status')}
                className="text-sm bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700 transition-colors"
              >
                Update Status
              </button>
              <button
                onClick={() => handleBulkAction('export')}
                className="text-sm bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700 transition-colors"
              >
                Export Selected
              </button>
            </div>
          )}
        </div>

        {/* Complaints List */}
        <div className="card p-6">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold text-gray-900">
              Complaints ({filteredComplaints.length})
            </h2>
            <div className="flex items-center space-x-2">
              <input
                type="checkbox"
                checked={selectedComplaints.length === filteredComplaints.length && filteredComplaints.length > 0}
                onChange={(e) => handleSelectAll(e.target.checked)}
                className="h-4 w-4 text-violet-600 focus:ring-violet-500 border-gray-300 rounded"
              />
              <span className="text-sm text-gray-600">Select All</span>
            </div>
          </div>

          <div className="space-y-4">
            {filteredComplaints.length === 0 ? (
              <div className="text-center py-12">
                <div className="text-6xl mb-4">ðŸ“‹</div>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">No complaints found</h3>
                <p className="text-gray-600">Try adjusting your filters or search terms</p>
              </div>
            ) : (
              filteredComplaints.map((complaint) => (
                <div key={complaint.id} className="border border-gray-200 rounded-lg p-4 hover:bg-gray-50 transition-colors">
                  <div className="flex items-start justify-between">
                    <div className="flex items-start space-x-4 flex-1">
                      <input
                        type="checkbox"
                        checked={selectedComplaints.includes(complaint.id)}
                        onChange={(e) => handleSelectComplaint(complaint.id, e.target.checked)}
                        className="h-4 w-4 text-violet-600 focus:ring-violet-500 border-gray-300 rounded mt-1"
                      />
                      
                      <div className="text-2xl">{getCategoryIcon(complaint.category)}</div>
                      
                      <div className="flex-1">
                        <div className="flex items-start justify-between mb-2">
                          <h3 className="font-semibold text-gray-900">{complaint.title}</h3>
                          <div className="flex space-x-2">
                            <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(complaint.status)}`}>
                              {complaint.status.replace('-', ' ').toUpperCase()}
                            </span>
                            <span className={`px-2 py-1 rounded-full text-xs font-medium ${getPriorityColor(complaint.priority)}`}>
                              {complaint.priority.toUpperCase()}
                            </span>
                          </div>
                        </div>
                        
                        <p className="text-sm text-gray-600 mb-2">{complaint.location}</p>
                        <p className="text-sm text-gray-700 mb-3">{complaint.description}</p>
                        
                        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-xs text-gray-500">
                          <div>
                            <span className="font-medium">Submitted by:</span> {complaint.submittedBy}
                          </div>
                          <div>
                            <span className="font-medium">Contact:</span> {complaint.contactInfo}
                          </div>
                          <div>
                            <span className="font-medium">Date:</span> {new Date(complaint.submittedAt).toLocaleDateString()}
                          </div>
                          <div>
                            <span className="font-medium">Assigned to:</span> {complaint.assignedTo || 'Unassigned'}
                          </div>
                        </div>

                        {/* Updates Timeline */}
                        {complaint.updates.length > 0 && (
                          <div className="mt-4 pt-4 border-t border-gray-200">
                            <h4 className="text-sm font-medium text-gray-700 mb-2">Updates</h4>
                            <div className="space-y-2">
                              {complaint.updates.slice(-2).map((update, index) => (
                                <div key={index} className="flex items-start space-x-2">
                                  <div className="w-2 h-2 bg-violet-500 rounded-full mt-1 flex-shrink-0"></div>
                                  <div className="flex-1">
                                    <p className="text-xs text-gray-700">{update.message}</p>
                                    <p className="text-xs text-gray-500">
                                      {new Date(update.date).toLocaleString()}
                                    </p>
                                  </div>
                                </div>
                              ))}
                            </div>
                          </div>
                        )}
                      </div>
                    </div>
                    
                    <div className="flex flex-col space-y-2 ml-4">
                      <button
                        onClick={() => handleAssignTask(complaint.id)}
                        className="text-xs bg-violet-600 text-white px-3 py-1 rounded hover:bg-violet-700 transition-colors"
                      >
                        Assign Task
                      </button>
                      <select
                        onChange={(e) => handleUpdateStatus(complaint.id, e.target.value)}
                        className="text-xs border border-gray-300 rounded px-2 py-1"
                        defaultValue={complaint.status}
                      >
                        <option value="pending">Pending</option>
                        <option value="in-progress">In Progress</option>
                        <option value="resolved">Resolved</option>
                        <option value="rejected">Rejected</option>
                      </select>
                      <button className="text-xs bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700 transition-colors">
                        View Details
                      </button>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ViewComplaints;
