import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useToast } from '../contexts/ToastContext';

const SubmitComplaint = () => {
  const navigate = useNavigate();
  const { showSuccess, showError } = useToast();
  
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    category: '',
    location: '',
    priority: 'medium',
    isAnonymous: false,
    contactInfo: ''
  });
  
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);

  const categories = [
    { value: 'road', label: 'Road Issues', icon: '🛣️' },
    { value: 'power', label: 'Power Outage', icon: '⚡' },
    { value: 'flooding', label: 'Flooding', icon: '🌊' },
    { value: 'garbage', label: 'Garbage Collection', icon: '🗑️' },
    { value: 'water', label: 'Water Supply', icon: '💧' },
    { value: 'streetlight', label: 'Street Light', icon: '💡' },
    { value: 'safety', label: 'Public Safety', icon: '🚨' },
    { value: 'other', label: 'Other', icon: '📋' }
  ];

  const priorities = [
    { value: 'low', label: 'Low', color: 'text-green-600' },
    { value: 'medium', label: 'Medium', color: 'text-yellow-600' },
    { value: 'high', label: 'High', color: 'text-red-600' },
    { value: 'urgent', label: 'Urgent', color: 'text-red-800' }
  ];

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleFileChange = (e) => {
    const selectedFiles = Array.from(e.target.files);
    setFiles(prev => [...prev, ...selectedFiles]);
  };

  const removeFile = (index) => {
    setFiles(prev => prev.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.title || !formData.description || !formData.category || !formData.location) {
      showError('Please fill in all required fields');
      return;
    }

    setLoading(true);

    try {
      const complaintData = {
        ...formData,
        files: files,
        submittedAt: new Date().toISOString(),
        status: 'pending'
      };

      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));
      
      showSuccess('Complaint submitted successfully! You will receive updates on the progress.');
      navigate('/dashboard');
    } catch (error) {
      showError('Failed to submit complaint. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100 py-12">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold gradient-text mb-4">
            Report a Community Issue
          </h1>
          <p className="text-xl text-gray-600">
            Help improve your community by reporting issues that need attention.
            <span className="font-semibold text-violet-600"> Free service for all Bangladeshi citizens.</span>
          </p>
        </div>

        <div className="card p-8">
          <form onSubmit={handleSubmit} className="space-y-8">
            {/* Issue Category */}
            <div>
              <label className="block text-lg font-semibold text-gray-700 mb-4">
                What type of issue are you reporting? *
              </label>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                {categories.map((category) => (
                  <label
                    key={category.value}
                    className={`relative cursor-pointer group ${
                      formData.category === category.value
                        ? 'ring-2 ring-violet-500 bg-violet-50'
                        : 'hover:bg-gray-50'
                    } border-2 border-gray-200 rounded-xl p-4 transition-all duration-200`}
                  >
                    <input
                      type="radio"
                      name="category"
                      value={category.value}
                      checked={formData.category === category.value}
                      onChange={handleChange}
                      className="sr-only"
                    />
                    <div className="text-center">
                      <div className="text-3xl mb-2">{category.icon}</div>
                      <div className="text-sm font-medium text-gray-700">
                        {category.label}
                      </div>
                    </div>
                  </label>
                ))}
              </div>
            </div>

            {/* Title and Priority */}
            <div className="grid md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  Issue Title *
                </label>
                <input
                  type="text"
                  name="title"
                  value={formData.title}
                  onChange={handleChange}
                  required
                  className="input-field"
                  placeholder="Brief description of the issue"
                />
              </div>
              
              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  Priority Level
                </label>
                <select
                  name="priority"
                  value={formData.priority}
                  onChange={handleChange}
                  className="input-field"
                >
                  {priorities.map((priority) => (
                    <option key={priority.value} value={priority.value}>
                      {priority.label}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            {/* Location */}
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">
                Location *
              </label>
              <input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleChange}
                required
                className="input-field"
                placeholder="Street address, area, or landmark"
              />
            </div>

            {/* Description */}
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">
                Detailed Description *
              </label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleChange}
                required
                rows="5"
                className="input-field resize-none"
                placeholder="Please provide detailed information about the issue, including when you first noticed it and any relevant details..."
              />
            </div>

            {/* Media Attachments */}
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">
                Attach Photos/Videos (Optional)
              </label>
              <div className="border-2 border-dashed border-gray-300 rounded-xl p-6 text-center hover:border-violet-400 transition-colors">
                <input
                  type="file"
                  multiple
                  accept="image/*,video/*"
                  onChange={handleFileChange}
                  className="hidden"
                  id="file-upload"
                />
                <label htmlFor="file-upload" className="cursor-pointer">
                  <div className="text-4xl mb-4">📷</div>
                  <div className="text-gray-600 mb-2">
                    <span className="font-semibold text-violet-600">Click to upload</span> or drag and drop
                  </div>
                  <div className="text-sm text-gray-500">
                    PNG, JPG, MP4 up to 10MB each
                  </div>
                </label>
              </div>
              
              {/* File Preview */}
              {files.length > 0 && (
                <div className="mt-4">
                  <h4 className="text-sm font-semibold text-gray-700 mb-2">Attached Files:</h4>
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                    {files.map((file, index) => (
                      <div key={index} className="relative bg-gray-100 rounded-lg p-3">
                        <div className="text-sm text-gray-600 truncate">{file.name}</div>
                        <button
                          type="button"
                          onClick={() => removeFile(index)}
                          className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full w-6 h-6 flex items-center justify-center text-xs hover:bg-red-600"
                        >
                          ×
                        </button>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>

            {/* Anonymous Option */}
            <div className="flex items-center space-x-3">
              <input
                type="checkbox"
                name="isAnonymous"
                checked={formData.isAnonymous}
                onChange={handleChange}
                className="h-4 w-4 text-violet-600 focus:ring-violet-500 border-gray-300 rounded"
              />
              <label className="text-sm text-gray-700">
                Submit anonymously (your contact information will not be shared)
              </label>
            </div>

            {/* Contact Information (if not anonymous) */}
            {!formData.isAnonymous && (
              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  Contact Information (Optional)
                </label>
                <input
                  type="text"
                  name="contactInfo"
                  value={formData.contactInfo}
                  onChange={handleChange}
                  className="input-field"
                  placeholder="Phone number or email for updates"
                />
                <p className="text-xs text-gray-500 mt-1">
                  We'll use this to send you updates about your complaint
                </p>
              </div>
            )}

            {/* Submit Button */}
            <div className="flex flex-col sm:flex-row gap-4 pt-6">
              <button
                type="submit"
                disabled={loading}
                className="btn-primary flex-1"
              >
                {loading ? (
                  <div className="flex items-center justify-center">
                    <div className="loading-spinner w-5 h-5 mr-2"></div>
                    Submitting...
                  </div>
                ) : (
                  'Submit Complaint'
                )}
              </button>
              
              <button
                type="button"
                onClick={() => navigate('/dashboard')}
                className="btn-secondary"
              >
                Cancel
              </button>
            </div>
          </form>
        </div>

        {/* Help Section */}
        <div className="mt-8 card p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Need Help?</h3>
          <div className="grid md:grid-cols-2 gap-6">
            <div>
              <h4 className="font-medium text-gray-700 mb-2">📞 Call Us</h4>
              <p className="text-gray-600">+880 1798 585 919</p>
              <p className="text-sm text-gray-500">Available 24/7 for emergencies</p>
            </div>
            <div>
              <h4 className="font-medium text-gray-700 mb-2">📧 Email Us</h4>
              <p className="text-gray-600">lognohassan@gmail.com</p>
              <p className="text-sm text-gray-500">We respond within 24 hours</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SubmitComplaint;
