import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import RoleDashboard from '../components/RoleDashboard';
import EnhancedSearchBar from '../components/EnhancedSearchBar';
import DataVisualization from '../components/DataVisualization';
import EnhancedLoading from '../components/EnhancedLoading';

const Home = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [currentFeatureIndex, setCurrentFeatureIndex] = useState(0);

  const features = [
    {
      icon: "🏛️",
      title: "Government Services",
      description: "Access essential government services and information in one place",
      image: "https://images.unsplash.com/photo-1554224155-6726b3ff858f?w=400&h=300&fit=crop&crop=center"
    },
    {
      icon: "📋",
      title: "Complaint Management",
      description: "Submit and track complaints with real-time updates and status tracking",
      image: "https://images.unsplash.com/photo-1554224155-6726b3ff858f?w=400&h=300&fit=crop&crop=center"
    },
    {
      icon: "📊",
      title: "Transparency",
      description: "View detailed reports and analytics on government services and responses",
      image: "https://images.unsplash.com/photo-1554224155-6726b3ff858f?w=400&h=300&fit=crop&crop=center"
    },
    {
      icon: "🤝",
      title: "Community Engagement",
      description: "Connect with local officials and participate in community decisions",
      image: "https://images.unsplash.com/photo-1554224155-6726b3ff858f?w=400&h=300&fit=crop&crop=center"
    }
  ];

  const services = [
    {
      name: "File Complaints",
      description: "Report issues and get them resolved quickly",
      icon: "📝",
      color: "from-violet-500 to-purple-600",
      image: "https://images.unsplash.com/photo-1554224155-6726b3ff858f?w=300&h=200&fit=crop&crop=center"
    },
    {
      name: "Track Status",
      description: "Monitor the progress of your complaints",
      icon: "📊",
      color: "from-emerald-500 to-teal-600",
      image: "https://images.unsplash.com/photo-1554224155-6726b3ff858f?w=300&h=200&fit=crop&crop=center"
    },
    {
      name: "Government Info",
      description: "Access official information and services",
      icon: "🏛️",
      color: "from-blue-500 to-indigo-600",
      image: "https://images.unsplash.com/photo-1554224155-6726b3ff858f?w=300&h=200&fit=crop&crop=center"
    }
  ];

  const stats = [
    { number: "10,000+", label: "Active Users", icon: "👥" },
    { number: "5,000+", label: "Complaints Resolved", icon: "✅" },
    { number: "95%", label: "Satisfaction Rate", icon: "⭐" },
    { number: "24/7", label: "Support Available", icon: "🕒" }
  ];

  const chartData = {
    labels: ['Infrastructure', 'Healthcare', 'Education', 'Transportation', 'Utilities'],
    datasets: [{
      data: [30, 25, 20, 15, 10],
      backgroundColor: [
        'rgba(124, 58, 237, 0.8)',
        'rgba(16, 185, 129, 0.8)',
        'rgba(245, 158, 11, 0.8)',
        'rgba(239, 68, 68, 0.8)',
        'rgba(59, 130, 246, 0.8)'
      ],
      borderColor: [
        'rgba(124, 58, 237, 1)',
        'rgba(16, 185, 129, 1)',
        'rgba(245, 158, 11, 1)',
        'rgba(239, 68, 68, 1)',
        'rgba(59, 130, 246, 1)'
      ],
      borderWidth: 2
    }]
  };

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentFeatureIndex((prev) => (prev + 1) % features.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [features.length]);

  const handleSearch = (query, filters) => {
    setIsLoading(true);
    // Simulate search
    setTimeout(() => setIsLoading(false), 2000);
  };

  // Check if user is logged in
  const isLoggedIn = localStorage.getItem('userInfo');
  const user = isLoggedIn ? JSON.parse(isLoggedIn) : null;

  if (isLoggedIn) {
    return <RoleDashboard user={user} />;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
        {/* Hero Section */}
      <section className="hero-section relative overflow-hidden">
        {/* Background Elements */}
        <div className="absolute inset-0 bg-gradient-to-br from-violet-600 via-purple-700 to-fuchsia-800">
          <div className="absolute inset-0 bg-[url('https://images.unsplash.com/photo-1554224155-6726b3ff858f?w=1920&h=1080&fit=crop&crop=center')] bg-cover bg-center opacity-20"></div>
          <div className="absolute inset-0 bg-black/30"></div>
            </div>

        {/* Floating 3D Elements */}
        <div className="absolute top-20 left-10 w-20 h-20 bg-white/10 rounded-full animate-3d-float backdrop-blur-sm"></div>
        <div className="absolute top-40 right-20 w-16 h-16 bg-white/20 rounded-full animate-3d-float backdrop-blur-sm" style={{animationDelay: '1s'}}></div>
        <div className="absolute bottom-40 left-20 w-24 h-24 bg-white/15 rounded-full animate-3d-float backdrop-blur-sm" style={{animationDelay: '2s'}}></div>

        <div className="hero-content">
          <h1 className="hero-title animate-fade-in-up">
            Welcome to <span className="gradient-text">CivicAid</span>
            </h1>
          <p className="hero-subtitle animate-fade-in-up" style={{animationDelay: '0.2s'}}>
            Empowering Bangladeshi communities through transparent government services and citizen engagement
          </p>

          {/* Enhanced Search Bar */}
          <div className="max-w-2xl mx-auto mb-8 animate-fade-in-up" style={{animationDelay: '0.4s'}}>
            <EnhancedSearchBar
              placeholder="Search for services, complaints, or information..."
              onSearch={handleSearch}
              suggestions={[
                'Government services',
                'File complaints',
                'Track status',
                'Community information',
                'Public services'
              ]}
              filters={[
                {
                  key: 'type',
                  label: 'Type',
                  options: [
                    { value: 'services', label: 'Services' },
                    { value: 'complaints', label: 'Complaints' },
                    { value: 'information', label: 'Information' }
                  ]
                }
              ]}
              debounceMs={300}
            />
          </div>

          <div className="hero-buttons animate-fade-in-up" style={{animationDelay: '0.6s'}}>
            <Link to="/submit-complaint" className="btn-primary">
              Submit Complaint
            </Link>
            <Link to="/services" className="btn-secondary">
              Explore Services
            </Link>
          </div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="stats-section">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl md:text-4xl font-bold text-center text-white mb-12">
            Our Impact in Numbers
          </h2>
          <div className="stats-grid">
            {stats.map((stat, index) => (
              <div key={index} className="stat-item animate-fade-in-up" style={{animationDelay: `${index * 0.1}s`}}>
                <div className="text-4xl mb-2">{stat.icon}</div>
                <div className="stat-number">{stat.number}</div>
                <div className="stat-label">{stat.label}</div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="py-20 bg-white/50 backdrop-blur-sm">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl md:text-4xl font-bold text-center text-gray-800 mb-16">
            Why Choose <span className="gradient-text">CivicAid</span>?
          </h2>

          <div className="features-grid">
            {features.map((feature, index) => (
              <div
                key={index}
                className={`feature-item group transition-all duration-500 hover:scale-105 ${
                  index === currentFeatureIndex ? 'ring-2 ring-violet-400 ring-opacity-50' : ''
                }`}
                style={{animationDelay: `${index * 0.2}s`}}
              >
                <div className="feature-icon group-hover:scale-110 transition-transform duration-300">
                  {feature.icon}
                </div>
                <h3 className="text-xl font-semibold text-gray-800 mb-3">{feature.title}</h3>
                <p className="text-gray-600 leading-relaxed">{feature.description}</p>

                {/* Background Image */}
                <div 
                  className="absolute inset-0 rounded-3xl opacity-10 group-hover:opacity-20 transition-opacity duration-300 -z-10"
                  style={{
                    backgroundImage: `url(${feature.image})`,
                    backgroundSize: 'cover',
                    backgroundPosition: 'center'
                  }}
                ></div>
          </div>
            ))}
          </div>
        </div>
      </section>

      {/* How It Works Section */}
      <section className="py-20 bg-gradient-to-r from-violet-50 to-purple-50">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl md:text-4xl font-bold text-center text-gray-800 mb-16">
            How It Works
          </h2>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-4xl mx-auto">
            {[
              { step: "1", title: "Submit", description: "Report your issue through our easy-to-use form", icon: "📝" },
              { step: "2", title: "Track", description: "Monitor progress with real-time updates", icon: "📊" },
              { step: "3", title: "Resolve", description: "Get your issue resolved efficiently", icon: "✅" }
            ].map((item, index) => (
              <div key={index} className="text-center group">
                <div className="w-20 h-20 mx-auto mb-4 rounded-full bg-gradient-to-br from-violet-500 to-purple-600 flex items-center justify-center text-2xl text-white font-bold shadow-2xl group-hover:scale-110 transition-transform duration-300">
                  {item.step}
            </div>
                <div className="text-3xl mb-3">{item.icon}</div>
                <h3 className="text-xl font-semibold text-gray-800 mb-2">{item.title}</h3>
                <p className="text-gray-600">{item.description}</p>
            </div>
            ))}
          </div>
        </div>
      </section>

        {/* Services Preview */}
      <section className="py-20 bg-white/80 backdrop-blur-sm">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl md:text-4xl font-bold text-center text-gray-800 mb-16">
            Our Services
          </h2>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {services.map((service, index) => (
              <div
                key={index}
                className="group relative overflow-hidden rounded-3xl shadow-2xl hover:shadow-3xl transition-all duration-500 hover:scale-105 hover:-translate-y-2"
                style={{animationDelay: `${index * 0.2}s`}}
              >
                {/* Background Image */}
                <div 
                  className="absolute inset-0 bg-cover bg-center transition-transform duration-500 group-hover:scale-110"
                  style={{backgroundImage: `url(${service.image})`}}
                >
                  <div className="absolute inset-0 bg-black/40"></div>
                </div>

                {/* Content */}
                <div className="relative z-10 p-6 text-white h-full flex flex-col justify-end">
                  <div className="text-4xl mb-3">{service.icon}</div>
                  <h3 className="text-xl font-semibold mb-2">{service.name}</h3>
                  <p className="text-white/90 mb-4">{service.description}</p>
                  <div className={`w-full h-1 bg-gradient-to-r ${service.color} rounded-full`}></div>
                </div>
              </div>
            ))}
          </div>

          <div className="text-center mt-12">
            <Link to="/services" className="btn-primary">
              View All Services
              </Link>
            </div>
            </div>
      </section>

      {/* Data Visualization Section */}
      <section className="py-20 bg-gradient-to-r from-indigo-50 to-blue-50">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl md:text-4xl font-bold text-center text-gray-800 mb-16">
            Issue Distribution
          </h2>

          <div className="max-w-2xl mx-auto">
