import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Navbar from '../components/Navbar';
import RoleDashboard from '../components/RoleDashboard';
import EnhancedSearchBar from '../components/EnhancedSearchBar';
import ProgressTracker from '../components/ProgressTracker';
import DashboardStats from '../components/DashboardStats';
import DataVisualization from '../components/DataVisualization';
import EnhancedLoading from '../components/EnhancedLoading';

const Home = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [activeFeature, setActiveFeature] = useState(0);

  const stats = [
    {
      title: 'Active Users',
      value: '10,000+',
      change: '+15%',
      changeType: 'positive',
      icon: '👥',
      description: 'Bangladeshi citizens'
    },
    {
      title: 'Issues Resolved',
      value: '500+',
      change: '+25%',
      changeType: 'positive',
      icon: '✅',
      description: 'Community problems fixed'
    },
    {
      title: 'Districts Covered',
      value: '64',
      change: '100%',
      changeType: 'positive',
      icon: '🗺️',
      description: 'All of Bangladesh'
    },
    {
      title: 'Free Service',
      value: '100%',
      change: 'Always',
      changeType: 'positive',
      icon: '💚',
      description: 'For all citizens'
    }
  ];

  const progressSteps = [
    {
      title: 'Report Issue',
      description: 'Submit community problems through our easy-to-use platform'
    },
    {
      title: 'Review & Assign',
      description: 'Our team reviews and assigns to appropriate authorities'
    },
    {
      title: 'Track Progress',
      description: 'Monitor real-time updates on issue resolution'
    },
    {
      title: 'Issue Resolved',
      description: 'Get notified when your community issue is fixed'
    }
  ];

  const chartData = [
    { label: 'Infrastructure', value: 35 },
    { label: 'Public Safety', value: 25 },
    { label: 'Environment', value: 20 },
    { label: 'Education', value: 15 },
    { label: 'Healthcare', value: 5 }
  ];

  const handleSearch = (query, filters) => {
    setIsLoading(true);
    console.log('Searching for:', query, 'with filters:', filters);

    // Simulate search delay
    setTimeout(() => {
      setIsLoading(false);
    }, 1500);
  };

  // Auto-rotate features
  useEffect(() => {
    const interval = setInterval(() => {
      setActiveFeature((prev) => (prev + 1) % 6);
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  // Check if user is logged in
  const isLoggedIn = localStorage.getItem('userInfo');
  const user = isLoggedIn ? JSON.parse(isLoggedIn) : null;

  if (isLoggedIn) {
    return <RoleDashboard user={user} />;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100 overflow-hidden">
      <Navbar />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12 relative z-10">
        {/* Hero Section */}
        <div className="text-center mb-16 animate-fade-in-up">
          <div className="mb-8">
            <div className="mx-auto h-24 w-24 bg-gradient-to-r from-violet-500 via-purple-600 to-fuchsia-700 rounded-3xl flex items-center justify-center mb-6 shadow-xl animate-float">
              <span className="text-2xl font-bold text-white font-['Plus_Jakarta_Sans'] tracking-wider">CivicAid</span>
            </div>
            <h1 className="text-5xl md:text-7xl font-bold text-gray-900 mb-6 animate-fade-in">
              Empowering <span className="gradient-text">Bangladeshi</span> Communities
            </h1>
            <p className="text-xl text-gray-600 max-w-4xl mx-auto leading-relaxed mb-8 animate-fade-in">
              Join thousands of Bangladeshi citizens making a difference in their communities. 
              <span className="font-semibold text-violet-600"> All services are completely free for Bangladeshi citizens.</span>
            </p>

            {/* Enhanced Search Bar */}
            <div className="max-w-2xl mx-auto mb-8">
              <EnhancedSearchBar
                placeholder="Search for community issues, services, or locations..."
                onSearch={handleSearch}
                suggestions={[
                  'Street light repair',
                  'Road maintenance',
                  'Water supply issues',
                  'Garbage collection',
                  'Public safety concerns',
                  'School infrastructure',
                  'Healthcare facilities',
                  'Public transportation'
                ]}
                filters={[
                  {
                    key: 'category',
                    label: 'Category',
                    options: [
                      { value: 'infrastructure', label: 'Infrastructure' },
                      { value: 'safety', label: 'Public Safety' },
                      { value: 'environment', label: 'Environment' },
                      { value: 'education', label: 'Education' },
                      { value: 'healthcare', label: 'Healthcare' }
                    ]
                  },
                  {
                    key: 'location',
                    label: 'Location',
                    options: [
                      { value: 'dhaka', label: 'Dhaka' },
                      { value: 'chittagong', label: 'Chittagong' },
                      { value: 'sylhet', label: 'Sylhet' },
                      { value: 'rajshahi', label: 'Rajshahi' },
                      { value: 'khulna', label: 'Khulna' }
                    ]
                  }
                ]}
                debounceMs={500}
                className="animate-bounce-in"
              />
            </div>

            {/* Loading State */}
            {isLoading && (
              <div className="mb-6 animate-fade-in">
                <EnhancedLoading 
                  type="dots" 
                  size="medium" 
                  text="Searching for solutions..." 
                  className="mx-auto"
                />
              </div>
            )}
          </div>

          {/* Action Buttons */}
          <div className="flex flex-col sm:flex-row gap-4 justify-center mb-12 animate-fade-in-up">
            <Link
              to="/register"
              className="btn-primary text-lg px-8 py-4 animate-pulse-slow"
            >
              Get Started Free
              <svg className="ml-2 h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7l5 5m0 0l-5 5m5-5H6" />
              </svg>
            </Link>
            <Link
              to="/services"
              className="btn-secondary text-lg px-8 py-4"
            >
              Explore Services
            </Link>
          </div>
        </div>

        {/* Statistics Section */}
        <div className="mb-16 animate-fade-in-up">
          <div className="text-center mb-12">
            <h2 className="text-4xl font-bold gradient-text mb-4">Our Impact Across Bangladesh</h2>
            <p className="text-xl text-gray-600">Making a difference in all 64 districts</p>
          </div>
          <DashboardStats stats={stats} />
        </div>

        {/* Data Visualization Section */}
        <div className="mb-16 animate-fade-in-up">
          <div className="text-center mb-12">
            <h2 className="text-4xl font-bold gradient-text mb-4">Issue Distribution</h2>
            <p className="text-xl text-gray-600">Types of community issues we help resolve</p>
          </div>
          <div className="card p-8">
            <DataVisualization
              type="chart"
              data={chartData}
              config={{ chartType: 'pie', height: 300 }}
              title="Community Issues by Category"
              description="Distribution of reported issues across different categories"
            />
          </div>
        </div>

        {/* How It Works Section */}
        <div className="mb-16 animate-fade-in-up">
          <div className="text-center mb-12">
            <h2 className="text-4xl font-bold gradient-text mb-4">How CivicAid Works</h2>
            <p className="text-xl text-gray-600">Simple steps to improve your community</p>
          </div>
          <div className="card p-8">
            <ProgressTracker 
              steps={progressSteps}
              currentStep={0}
              variant="default"
            />
          </div>
        </div>

        {/* Features Section */}
        <div className="mb-16 animate-fade-in-up">
          <div className="text-center mb-12">
            <h2 className="text-4xl font-bold gradient-text mb-4">Why Choose CivicAid?</h2>
            <p className="text-xl text-gray-600">The most comprehensive civic engagement platform in Bangladesh</p>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
            {[
              {
                icon: '🆓',
                title: '100% Free',
                description: 'All services completely free for Bangladeshi citizens. No hidden costs, no subscription fees.',
                color: 'from-emerald-500 to-teal-600'
              },
              {
                icon: '🌍',
                title: 'Nationwide Coverage',
                description: 'Available in all 64 districts of Bangladesh. No matter where you are, we\'re here to help.',
                color: 'from-blue-500 to-indigo-600'
              },
              {
                icon: '⚡',
                title: 'Fast Response',
                description: 'Quick issue resolution with real-time tracking and updates on progress.',
                color: 'from-yellow-500 to-orange-600'
              },
              {
                icon: '🤝',
                title: 'Community Focus',
                description: 'Built specifically for Bangladeshi communities with local language support.',
                color: 'from-purple-500 to-pink-600'
              },
              {
                icon: '📱',
                title: 'Easy to Use',
                description: 'Simple, intuitive interface that works on all devices - mobile, tablet, and desktop.',
                color: 'from-indigo-500 to-purple-600'
              },
              {
                icon: '🔒',
                title: 'Secure & Private',
                description: 'Your data is protected with industry-standard security measures.',
                color: 'from-red-500 to-pink-600'
              }
            ].map((feature, index) => (
              <div 
                key={index}
                className={`card p-6 text-center group transition-all duration-500 hover:scale-105 ${
                  activeFeature === index ? 'ring-4 ring-violet-300 shadow-2xl' : ''
                }`}
                onMouseEnter={() => setActiveFeature(index)}
              >
                <div className={`text-4xl mb-4 transform transition-transform duration-300 ${
                  activeFeature === index ? 'scale-110' : ''
                }`}>
                  {feature.icon}
                </div>
                <h3 className="text-xl font-bold text-gray-900 mb-3">{feature.title}</h3>
                <p className="text-gray-600">{feature.description}</p>
                <div className={`mt-4 h-1 bg-gradient-to-r ${feature.color} rounded-full transform transition-all duration-300 ${
                  activeFeature === index ? 'scale-x-110' : 'scale-x-0'
                }`}></div>
              </div>
            ))}
          </div>
        </div>

        {/* Services Preview */}
        <div className="mb-16 animate-fade-in-up">
          <div className="text-center mb-12">
            <h2 className="text-4xl font-bold gradient-text mb-4">Services for Everyone</h2>
            <p className="text-xl text-gray-600">Comprehensive solutions for all community needs</p>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-6">
            {[
              {
                icon: '👤',
                title: 'For Citizens',
                description: 'Report issues, track progress, and stay informed about your community.',
                link: '/services',
                color: 'from-violet-500 to-purple-600'
              },
              {
                icon: '🤲',
                title: 'For Volunteers',
                description: 'Find opportunities to help your community and make a positive impact.',
                link: '/services',
                color: 'from-emerald-500 to-teal-600'
              },
              {
                icon: '🏢',
                title: 'For NGOs',
                description: 'Connect with communities and coordinate social development projects.',
                link: '/services',
                color: 'from-blue-500 to-indigo-600'
              },
              {
                icon: '🏛️',
                title: 'For Authorities',
                description: 'Efficiently manage and respond to community issues and requests.',
                link: '/services',
                color: 'from-orange-500 to-red-600'
              }
            ].map((service, index) => (
              <div 
                key={index}
                className="card p-6 text-center group hover:scale-105 transition-all duration-300 hover-lift"
              >
                <div className={`text-3xl mb-4 p-3 rounded-full bg-gradient-to-r ${service.color} text-white transform transition-transform duration-300 group-hover:scale-110`}>
                  {service.icon}
                </div>
                <h3 className="text-lg font-bold text-gray-900 mb-2">{service.title}</h3>
                <p className="text-sm text-gray-600 mb-4">{service.description}</p>
                <Link 
                  to={service.link} 
                  className="text-violet-600 hover:text-violet-700 font-semibold text-sm inline-flex items-center group-hover:translate-x-1 transition-transform duration-300"
                >
                  Learn More →
                </Link>
              </div>
            ))}
          </div>
        </div>

        {/* CTA Section */}
        <div className="text-center animate-fade-in-up">
          <div className="bg-gradient-to-r from-violet-600 via-purple-600 to-fuchsia-600 rounded-3xl p-12 text-white shadow-2xl hover:shadow-3xl transition-all duration-500">
            <h2 className="text-4xl font-bold mb-4">Ready to Make a Difference?</h2>
            <p className="text-xl mb-8 opacity-90 max-w-2xl mx-auto">
              Join thousands of Bangladeshi citizens who are already improving their communities. 
              <span className="font-semibold"> It's completely free!</span>
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link
                to="/register"
                className="inline-flex items-center px-8 py-4 bg-white text-violet-600 font-semibold rounded-2xl hover:bg-gray-100 transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl"
              >
                Start Your Journey
                <svg className="ml-3 h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7l5 5m0 0l-5 5m5-5H6" />
                </svg>
              </Link>
              <Link
                to="/contact"
                className="inline-flex items-center px-8 py-4 border-2 border-white text-white font-semibold rounded-2xl hover:bg-white hover:text-violet-600 transition-all duration-300 transform hover:scale-105"
              >
                Contact Our Team
              </Link>
            </div>
            <div className="mt-6 text-sm opacity-75">
              <p>📞 Call us: +880 1798 585 919 | 📧 Email: lognohassan@gmail.com</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
